        "url": "https://synergybrandarchitect.in",
        "telephone": "+919525230232",
        "address": organizationData.address,
        "geo": {
          "@type": "GeoCoordinates",
          "latitude": "25.5941",
          "longitude": "85.1376"
        },
        "openingHoursSpecification": {
          "@type": "OpeningHoursSpecification",
          "dayOfWeek": [
            "Monday",
