import { ReactNode, useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { getQueryFn } from "@/lib/queryClient";
import { Menu } from "lucide-react";
import { Button } from "@/components/ui/button";
import AdminSidebar from "./sidebar";

interface AdminLayoutProps {
  children: ReactNode;
  title?: string;
  description?: string;
}

export default function AdminLayout({ children, title, description }: AdminLayoutProps) {
  const [, setLocation] = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState<boolean | null>(null);

  // Authentication check
  const authQuery = useQuery({
    queryKey: ['/api/auth/check'],
    queryFn: getQueryFn<{authenticated: boolean; user: any}>({on401: 'returnNull'}),
  });

  // Check auth status and redirect if not authenticated
  useEffect(() => {
    if (authQuery.isSuccess) {
      setIsAuthenticated(!!authQuery.data?.authenticated);
      if (!authQuery.data?.authenticated) {
        setLocation('/admin/login');
      }
    } else if (authQuery.isError) {
      setLocation('/admin/login');
    }
  }, [authQuery.isSuccess, authQuery.isError, authQuery.data, setLocation]);

  // Loading state
  if (isAuthenticated === null) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">Loading...</h2>
          <p className="text-muted-foreground">Please wait while we verify your credentials</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen overflow-hidden bg-slate-50">
      {/* Sidebar - desktop */}
      <div className="hidden md:block">
        <AdminSidebar />
      </div>

      {/* Sidebar - mobile */}
      {sidebarOpen && (
        <div className="fixed inset-0 z-50 md:hidden">
          <div className="fixed inset-0 bg-black/50" onClick={() => setSidebarOpen(false)} />
          <div className="fixed inset-y-0 left-0 w-full max-w-xs">
            <AdminSidebar />
          </div>
        </div>
      )}

      {/* Main content */}
      <div className="flex flex-1 flex-col overflow-hidden">
        {/* Mobile header */}
        <div className="md:hidden flex h-16 items-center border-b bg-white px-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setSidebarOpen(true)}
            className="mr-2"
          >
            <Menu className="h-5 w-5" />
          </Button>

          <div className="flex items-center flex-1">
            <img 
              src="//i.imgur.com/8j3VafC.png" 
              alt="Logo" 
              className="h-8 w-auto mr-2" 
            />
            <div>
              <span className="font-bold text-[#FF6B00]">Synergy</span>
              <span className="font-medium text-gray-700">Brand</span>
            </div>
          </div>
        </div>

        {/* Page content */}
        <div className="flex-1 overflow-auto">
          {(title || description) && (
            <div className="border-b p-6 bg-white">
              {title && <h1 className="text-xl font-semibold mb-1">{title}</h1>}
              {description && <p className="text-gray-500">{description}</p>}
            </div>
          )}
          <div className="p-6">
            {children}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/admin/needs-auth.t.content:
import { ReactNode, useEffect } from 'react';
import { useLocation } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { getQueryFn } from '@/lib/queryClient';

interface AdminNeedsAuthProps {
  children: ReactNode;
}

export default function AdminNeedsAuth({ children }: AdminNeedsAuthProps) {
  const [, setLocation] = useLocation();

  // Authentication check
  const { data, isLoading, isError } = useQuery({
    queryKey: ['/api/auth/check'],
    queryFn: getQueryFn<{authenticated: boolean; user: any}>({on401: 'returnNull'}),
  });

  // Check auth status and redirect if not authenticated
  useEffect(() => {
    if (!isLoading) {
      if (isError || !data?.authenticated) {
        setLocation('/admin/login');
      }
    }
  }, [isLoading, isError, data, setLocation]);

  // Loading state
  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">Loading...</h2>
          <p className="text-muted-foreground">Please wait while we verify your credentials</p>
        </div>
      </div>
    );
  }

  // Not authenticated
  if (isError || !data?.authenticated) {
    return null;  // Return null as we're redirecting in the useEffect
  }

  // Authenticated, render children
  return <>{children}</>;
}

client/src/components/admin/rich-text-ed.content:
import ReactQuill from 'react-quill';
import { forwardRef, useImperativeHandle, useRef, useEffect, useState } from 'react';

interface RichTextEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
}

const RichTextEditor = forwardRef<any, RichTextEditorProps>(
  ({ value, onChange, placeholder = "Write your content here..." }, ref) => {
    const quillRef = useRef<any>(null);

    // Log when the value prop changes to help with debugging
    useEffect(() => {
      console.log('RichTextEditor value updated:', value ? `${value.substring(0, 30)}...` : 'empty');
    }, [value]);

    useImperativeHandle(ref, () => ({
      focus: () => {
        if (quillRef.current) {
          quillRef.current.focus();
        }
      },
      clear: () => {
        if (quillRef.current) {
          quillRef.current.getEditor().setText('');
        }
      },
      getEditor: () => {
        if (quillRef.current) {
          return quillRef.current.getEditor();
        }
        return null;
      },
      setValue: (content: string) => {
        if (quillRef.current) {
          const editor = quillRef.current.getEditor();
          editor.clipboard.dangerouslyPasteHTML(content);
        }
      }
    }));

    const modules = {
      toolbar: [
        [{ 'header': [1, 2, 3, false] }],
        ['bold', 'italic', 'underline', 'strike'],
        [{ 'list': 'ordered'}, { 'list': 'bullet' }],
        [{ 'indent': '-1'}, { 'indent': '+1' }],
        ['link', 'image'],
        ['clean'],
        [{ 'color': [] }, { 'background': [] }],
        [{ 'align': [] }]
      ]
    };

    return (
      <ReactQuill
        ref={quillRef}
        theme="snow"
        value={value || ''}
        onChange={onChange}
        modules={modules}
        placeholder={placeholder}
        preserveWhitespace={true}
      />
    );
  }
);

RichTextEditor.displayName = 'RichTextEditor';

export default RichTextEditor;

